export default {
	orderStatusOptions:   [
	{
    "label": "Order Placed",
    "value": "ORDER PLACED"
  },
  {
    "label": "Delayed",
    "value": "DELAYED"
  },
  {
    "label": "Delivered",
    "value": "DELIVERED"
  },
  {
    "label": "Refunded",
    "value": "REFUNDED"
  }
],
	updateOrder: async () =>{
		await edit_order.run();
			await fetch_orders.run();
			return closeModal('edit_order_modal');	
	},
	refundOrder: async () => {
		await StripeRefund.run();
		await update_order_amount.run();
		return await fetch_orders.run();
	},
	addOrder: async () => {
		const orderId = this.generateRandomID();
		await add_order.run({Id: orderId});
		await add_product.run({Id: orderId});
		await fetch_orders.run();
		await resetWidget('add_modal');
		return closeModal('add_modal');
	},
	generateRandomID: () => {
		return '_' + Math.random().toString(36).substr(2,9);

	}
}
